write_file({'path': 'app/frontend/src/contexts/AuthContext.jsx', 'content': "import React, { createContext, useContext, useEffect, useState } from 'react';
import { getCurrentUser, login as apiLogin, logout as apiLogout, register as apiRegister } from '../services/api.js';

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function init() {
      try {
        const me = await getCurrentUser();
        setUser(me);
      } catch (e) {
        // not logged in
      } finally {
        setLoading(false);
      }
    }
    init();
  }, []);

  const login = async (email, password) => {
    const { user: loggedInUser, accessToken: token } = await apiLogin({ email, password });
    setUser(loggedInUser);
    setAccessToken(token);
  };

  const register = async (data) => {
    const { user: newUser, accessToken: token } = await apiRegister(data);
    setUser(newUser);
    setAccessToken(token);
  };

  const logout = async () => {
    try {
      await apiLogout();
    } catch (e) {
      // ignore
    }
    setUser(null);
    setAccessToken(null);
  };

  const value = {
    user,
    accessToken,
    loading,
    login,
    logout,
    register,
    isAuthenticated: !!user,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error('useAuth must be used within AuthProvider');
  return ctx;
}
"})

write_file({'path': 'app/frontend/src/services/api.js', 'content': "const API_BASE = '/api/v1';

async function request(path, options = {}) {
  const res = await fetch(`${API_BASE}${path}`, {
    headers: {
      'Content-Type': 'application/json',
      ...(options.headers || {}),
    },
    credentials: 'include',
    ...options,
  });

  if (!res.ok) {
    let errorBody;
    try {
      errorBody = await res.json();
    } catch (e) {
      throw new Error(res.statusText || 'Request failed');
    }
    const message = errorBody?.error?.message || 'Request failed';
    const error = new Error(message);
    error.code = errorBody?.error?.code;
    error.details = errorBody?.error?.details;
    throw error;
  }

  if (res.status === 204) return null;

  return res.json();
}

// Auth
export async function login(body) {
  const data = await request('/auth/login', {
    method: 'POST',
    body: JSON.stringify(body),
  });
  return data;
}

export async function register(body) {
  const data = await request('/auth/register', {
    method: 'POST',
    body: JSON.stringify(body),
  });
  return data;
}

export async function logout() {
  await request('/auth/logout', { method: 'POST' });
}

// Users
