write_file({'path': 'app/frontend/src/contexts/AuthContext.jsx', 'content': "import React, { createContext, useContext, useEffect, useMemo, useState } from 'react';
import { getProfile, login as apiLogin, register as apiRegister } from '../services/api.js';

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(() => localStorage.getItem('authToken'));
  const [loading, setLoading] = useState(!!token);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchProfile() {
      if (!token) return;
      setLoading(true);
      try {
        const profile = await getProfile(token);
        setUser(profile);
        setError(null);
      } catch (err) {
        console.error('Failed to load profile', err);
        setUser(null);
        setToken(null);
        localStorage.removeItem('authToken');
      } finally {
        setLoading(false);
      }
    }
    fetchProfile();
  }, [token]);

  const login = async (credentials) => {
    setError(null);
    const { token: newToken, user: loggedInUser } = await apiLogin(credentials);
    setToken(newToken);
    setUser(loggedInUser);
    localStorage.setItem('authToken', newToken);
  };

  const register = async (payload) => {
    setError(null);
    await apiRegister(payload);
    // After register, auto-login
    await login({ email: payload.email, password: payload.password });
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('authToken');
  };

  const value = useMemo(
    () => ({ user, token, loading, error, login, register, logout }),
    [user, token, loading, error]
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error('useAuth must be used within AuthProvider');
  return ctx;
}
"})

