async function request(path, { method = 'GET', token, body, headers, ...rest } = {}) {
  const res = await fetch(`${API_BASE}${path}`, {
    method,
    headers: {
      'Content-Type': body ? 'application/json' : undefined,
      ...(token ? { Authorization: `Bearer ${token}` } : {}),
      ...headers
    },
    body: body ? JSON.stringify(body) : undefined,
    ...rest
  });

  if (!res.ok) {
    let errorBody;
    try {
      errorBody = await res.json();
    } catch {
      // ignore
    }
    const err = new Error(errorBody?.message || `Request failed with ${res.status}`);
    err.status = res.status;
    err.body = errorBody;
    throw err;
  }

  if (res.status === 204) return null;
  return res.json();
}

export async function login({ email, password }) {
  const data = await request('/auth/login', {
    method: 'POST',
    body: { email, password }
  });
  return data;
}

export async function register(payload) {
  const data = await request('/auth/register', {
    method: 'POST',
    body: payload
  });
  return data;
}

export async function getProfile(token) {
  return request('/profile', { token });
}

export async function searchFlights(params) {
  const query = new URLSearchParams(params).toString();
  const data = await request(`/flights/search?${query}`);
  return data.items;
}

export async function getFlight(id) {
  return request(`/flights/${id}`);
}

export async function searchHotels(params) {
  const query = new URLSearchParams(params).toString();
  const data = await request(`/hotels/search?${query}`);
  return data.items;
}

export async function getHotel(id) {
  return request(`/hotels/${id}`);
}

export async function getHotelRooms(id) {
  const data = await request(`/hotels/${id}/rooms`);
  return data.items ?? data;
}

export async function getBookings(token) {
  const data = await request('/bookings', { token });
  return data.items ?? data;
}

export async function getBooking(id, token) {
  return request(`/bookings/${id}`, { token });
}

export async function createBooking(payload, token) {
  return request('/bookings', { method: 'POST', body: payload, token });
}

export { request };
"})

