write_file({'path': 'app/frontend/src/contexts/AuthContext.jsx', 'content': "import React, { createContext, useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { api } from '../services/api.js';

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(() => localStorage.getItem('voyagex_token'));
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchMe() {
      if (!token) {
        setLoading(false);
        return;
      }
      try {
        const me = await api.getMe(token);
        setUser(me);
      } catch (e) {
        console.error('Failed to fetch current user', e);
        setToken(null);
        localStorage.removeItem('voyagex_token');
      } finally {
        setLoading(false);
      }
    }
    fetchMe();
  }, [token]);

  const login = async (email, password) => {
    const res = await api.login(email, password);
    setToken(res.token);
    localStorage.setItem('voyagex_token', res.token);
    setUser(res.user);
    return res;
  };

  const register = async (payload) => {
    const res = await api.register(payload);
    setToken(res.token);
    localStorage.setItem('voyagex_token', res.token);
    setUser(res.user);
    return res;
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('voyagex_token');
    navigate('/');
  };

  const value = { user, token, loading, login, register, logout };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  return useContext(AuthContext);
}
"})

write_file({'path': 'app/frontend/src/services/api.js', 'content': "import axios from 'axios';

const client = axios.create({
  baseURL: '/api',
});

client.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API error', error);
    throw error;
  },
);

export const api = {
  setToken(token) {
    if (token) {
      client.defaults.headers.common.Authorization = `Bearer ${token}`;
    } else {
      delete client.defaults.headers.common.Authorization;
    }
  },
  async login(email, password) {
    const { data } = await client.post('/auth/login', { email, password });
    this.setToken(data.token);
    return data;
  },
  async register(payload) {
    const { data } = await client.post('/auth/register', payload);
    this.setToken(data.token);
    return data;
  },
  async getMe(tokenOverride) {
    if (tokenOverride) {
      this.setToken(tokenOverride);
    }
    const { data } = await client.get('/auth/me');
    return data;
  },
  async updateProfile(payload) {
    const { data } = await client.put('/auth/me', payload);
    return data;
  },
  async searchFlights(params) {
    const { data } = await client.get('/flights', { params });
    return data;
  },
  async getFlight(id) {
    const { data } = await client.get(`/flights/${id}`);
    return data;
  },
  async searchHotels(params) {
    const { data } = await client.get('/hotels', { params });
    return data;
  },
  async getHotel(id) {
    const { data } = await client.get(`/hotels/${id}`);
    return data;
  },
  async searchCars(params) {
    const { data } = await client.get('/cars', { params });
    return data;
  },
  async searchPackages(params) {
    const { data } = await client.get('/packages', { params });
    return data;
  },
  async getCart() {
    const { data } = await client.get('/cart');
    return data;
  },
  async addToCart(item) {
    const { data } = await client.post('/cart/items', item);
    return data;
  },
  async removeCartItem(id) {
    const { data } = await client.delete(`/cart/items/${id}`);
    return data;
  },
  async checkout(payload) {
    const { data } = await client.post('/checkout', payload);
    return data;
  },
  async getTrips(params) {
    const { data } = await client.get('/trips', { params });
    return data;
  },
  async getTrip(id) {
    const { data } = await client.get(`/trips/${id}`);
    return data;
  },
  async getSavedPayments() {
    const { data } = await client.get('/payments');
    return data;
  },
};
"})

