write_file({'path': 'design/spec.requirements.json', 'content': '{
  "project_name": "VoyageX Travel Platform",
  "description": "An Expedia-style travel booking platform for flights, hotels, car rentals, and package deals with full user authentication, shopping cart, checkout, and trip management.",
  "tech_stack": {
    "frontend": {
      "framework": "React",
      "bundler": "Vite",
      "styling": "TailwindCSS"
    },
    "backend": {
      "runtime": "Node.js",
      "framework": "Express",
      "auth": "JWT with bcrypt"
    },
    "database": {
      "engine": "PostgreSQL"
    }
  },
  "features": [
    {
      "name": "User Authentication & Profiles",
      "description": "Email/password registration and login with JWT-based authentication, profile management, and booking history.",
      "priority": "P0"
    },
    {
      "name": "Flight Search & Booking",
      "description": "Search, filter, sort, and book flights with seat class selection and cart integration.",
      "priority": "P0"
    },
    {
      "name": "Hotel Search & Booking",
      "description": "Search, filter, and book hotels with room type selection, photo galleries, amenities, and reviews.",
      "priority": "P0"
    },
    {
      "name": "Car Rental Search & Booking",
      "description": "Search and book rental cars with filters, extras, and cart integration.",
      "priority": "P1"
    },
    {
      "name": "Package Deals",
      "description": "Bundle flights, hotels, and cars into discounted packages and compare with individual pricing.",
      "priority": "P1"
    },
    {
      "name": "Shopping Cart & Checkout",
      "description": "Multi-item cart for flights, hotels, and cars with promo codes, taxes/fees breakdown, and secure payment form.",
      "priority": "P0"
    },
    {
      "name": "Trip Management",
      "description": "View upcoming and past trips, download confirmations, and cancel or modify bookings.",
      "priority": "P1"
    },
    {
      "name": "Favorites & VIP Access",
      "description": "Heart-based favorites for hotels and other products plus VIP Access badges on eligible properties.",
      "priority": "P2"
    }
  ],
  "pages": [
    {
      "name": "Home",
      "path": "/",
      "components": ["MainSearchTabs", "HeroBanner", "FeaturedDealsCarousel"],
      "description": "Landing page with tabbed search for Stays/Flights/Cars/Packages and highlighted deals."
    },
    {
      "name": "Auth - Login",
      "path": "/login",
      "components": ["AuthForm", "SocialProofStrip"],
      "description": "Email/password login with link to registration and password reset placeholder."
    },
    {
      "name": "Auth - Register",
      "path": "/register",
      "components": ["AuthForm"],
      "description": "User registration form capturing name, email, password, and phone."
    },
    {
      "name": "Flights Search Results",
      "path": "/flights",
      "components": ["SearchFiltersSidebar", "FlightResultCard", "SortBar", "Pagination"],
      "description": "Flight search results list with filters and sort controls."
    },
    {
      "name": "Flight Details",
      "path": "/flights/:id",
      "components": ["FlightItineraryTimeline", "FareOptions", "AddToCartButton"],
      "description": "Detailed flight view with segments, layovers, and seat classes."
    },
    {
      "name": "Hotels Search Results",
      "path": "/stays",
      "components": ["SearchFiltersBar", "HotelResultCard", "MapToggle", "Pagination"],
      "description": "Hotel search results with filters, map view toggle, and VIP Access badges."
    },
    {
      "name": "Hotel Details",
      "path": "/stays/:id",
      "components": ["ImageCarousel", "HotelSummary", "RoomTypeList", "ReviewsSection", "MapSection", "FavoriteHeart"],
      "description": "Hotel detail page with photos, amenities, room options, and reviews."
    },
    {
      "name": "Cars Search Results",
      "path": "/cars",
      "components": ["SearchFiltersBar", "CarResultCard", "ExtrasSelector"],
      "description": "Car rental search results with filters and extras add-on."
    },
    {
      "name": "Packages Search Results",
      "path": "/packages",
      "components": ["PackageResultCard", "ComparePriceBreakdown"],
      "description": "Package deals list comparing bundle vs individual pricing."
    },
    {
      "name": "Shopping Cart",
      "path": "/cart",
      "components": ["CartItemList", "PriceSummary", "PromoCodeForm"],
      "description": "Multi-item cart showing flights, hotels, cars, and package items with cost breakdown."
    },
    {
      "name": "Checkout",
      "path": "/checkout",
      "components": ["TravelerDetailsForm", "PaymentForm", "OrderSummary"],
      "description": "Secure checkout page collecting traveler and payment details and confirming booking."
    },
    {
      "name": "Trips",
      "path": "/trips",
      "components": ["TripsList", "TripCard", "TripFilters"],
      "description": "Dashboard of upcoming and past trips with actions to view, modify, or cancel."
    },
    {
      "name": "Profile",
      "path": "/profile",
      "components": ["ProfileForm", "SavedPaymentsList", "SecuritySettings"],
      "description": "User profile management including contact info and saved payment methods."
    }
  ],
  "api_endpoints": [
    {"method": "POST", "path": "/auth/register", "description": "Register a new user", "auth_required": false},
    {"method": "POST", "path": "/auth/login", "description": "Login and get JWT", "auth_required": false},
    {"method": "GET", "path": "/auth/me", "description": "Get current user profile", "auth_required": true},
    {"method": "PUT", "path": "/auth/me", "description": "Update current user profile", "auth_required": true},

    {"method": "GET", "path": "/flights", "description": "Search flights with query params for origin, destination, dates, passengers, filters, and sort", "auth_required": false},
    {"method": "GET", "path": "/flights/:id", "description": "Get flight details by ID", "auth_required": false},

    {"method": "GET", "path": "/hotels", "description": "Search hotels with filters for location, dates, guests, price, rating, and amenities", "auth_required": false},
    {"method": "GET", "path": "/hotels/:id", "description": "Get hotel details by ID including rooms and reviews", "auth_required": false},

    {"method": "GET", "path": "/cars", "description": "Search rental cars with filters for location, dates, type, price, and company", "auth_required": false},
    {"method": "GET", "path": "/cars/:id", "description": "Get car details by ID", "auth_required": false},

    {"method": "GET", "path": "/packages", "description": "Search package deals combining flights, hotels, and cars", "auth_required": false},

    {"method": "GET", "path": "/cart", "description": "Get current user's cart items", "auth_required": true},
    {"method": "POST", "path": "/cart/items", "description": "Add an item (flight, hotel, car, or package) to cart", "auth_required": true},
    {"method": "PUT", "path": "/cart/items/:id", "description": "Update quantity/details of a cart item", "auth_required": true},
    {"method": "DELETE", "path": "/cart/items/:id", "description": "Remove an item from cart", "auth_required": true},

    {"method": "POST", "path": "/checkout", "description": "Create bookings and process payment for current cart", "auth_required": true},

    {"method": "GET", "path": "/trips", "description": "List upcoming and past trips for current user", "auth_required": true},
    {"method": "GET", "path": "/trips/:id", "description": "Get details for a single trip including all associated bookings", "auth_required": true},
    {"method": "POST", "path": "/trips/:id/cancel", "description": "Cancel a trip and associated bookings (subject to rules)", "auth_required": true},

    {"method": "GET", "path": "/favorites", "description": "List current user's favorite properties/items", "auth_required": true},
    {"method": "POST", "path": "/favorites", "description": "Add a favorite item (e.g., hotel)", "auth_required": true},
    {"method": "DELETE", "path": "/favorites/:id", "description": "Remove a favorite item", "auth_required": true}
  ],
  "data_models": [
    {
      "entity": "User",
      "fields": {
        "id": "UUID primary key",
        "name": "string",
        "email": "string unique",
        "password_hash": "string (bcrypt)",
        "phone": "string nullable",
        "created_at": "timestamp",
        "updated_at": "timestamp"
      },
      "relationships": {
        "bookings": "User has many Trip and Booking records",
        "favorites": "User has many Favorite records",
        "payment_methods": "User has many PaymentMethod records"
      }
    },
    {
      "entity": "PaymentMethod",
      "fields": {
        "id": "UUID primary key",
        "user_id": "UUID references User",
        "brand": "string (e.g., Visa)",
        "last4": "string(4)",
        "exp_month": "integer",
        "exp_year": "integer",
        "cardholder_name": "string",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "PaymentMethod belongs to User"
      }
    },
    {
      "entity": "Flight",
      "fields": {
        "id": "UUID primary key",
        "airline": "string",
        "flight_number": "string",
        "origin": "string (IATA code)",
        "destination": "string (IATA code)",
        "departure_time": "timestamp",
        "arrival_time": "timestamp",
        "duration_minutes": "integer",
        "stops": "integer",
        "base_price": "numeric(10,2)",
        "cabin_classes": "jsonb (prices for Economy/Business/First)"
      },
      "relationships": {}
    },
    {
      "entity": "Hotel",
      "fields": {
        "id": "UUID primary key",
        "name": "string",
        "location": "string (city, country)",
        "latitude": "numeric",
        "longitude": "numeric",
        "star_rating": "numeric(2,1)",
        "description": "text",
        "amenities": "text[]",
        "is_vip_access": "boolean default false",
        "thumbnail_url": "string"
      },
      "relationships": {
        "rooms": "Hotel has many RoomType records",
        "reviews": "Hotel has many Review records"
      }
    },
    {
      "entity": "RoomType",
      "fields": {
        "id": "UUID primary key",
        "hotel_id": "UUID references Hotel",
        "name": "string",
        "bed_configuration": "string",
        "max_guests": "integer",
        "price_per_night": "numeric(10,2)",
        "is_refundable": "boolean",
        "includes_breakfast": "boolean"
      },
      "relationships": {
        "hotel": "RoomType belongs to Hotel"
      }
    },
    {
      "entity": "Car",
      "fields": {
        "id": "UUID primary key",
        "company": "string",
        "model": "string",
        "car_type": "string (e.g., Economy, SUV)",
        "seats": "integer",
        "transmission": "string (Automatic/Manual)",
        "fuel_type": "string",
        "base_price_per_day": "numeric(10,2)"
      },
      "relationships": {}
    },
    {
      "entity": "PackageDeal",
      "fields": {
        "id": "UUID primary key",
        "name": "string",
        "description": "text",
        "flight_id": "UUID references Flight",
        "hotel_id": "UUID references Hotel",
        "car_id": "UUID references Car nullable",
        "bundle_price": "numeric(10,2)",
        "savings_amount": "numeric(10,2)"
      },
      "relationships": {
        "flight": "PackageDeal belongs to Flight",
        "hotel": "PackageDeal belongs to Hotel",
        "car": "PackageDeal optionally belongs to Car"
      }
    },
    {
      "entity": "Trip",
      "fields": {
        "id": "UUID primary key",
        "user_id": "UUID references User",
        "start_date": "date",
        "end_date": "date",
        "status": "string (upcoming, completed, cancelled)",
        "created_at": "timestamp"
      },
      "relationships": {
        "bookings": "Trip has many Booking records"
      }
    },
    {
      "entity": "Booking",
      "fields": {
        "id": "UUID primary key",
        "trip_id": "UUID references Trip",
        "user_id": "UUID references User",
        "type": "string (flight, hotel, car, package)",
        "reference_id": "UUID (ID of Flight/Hotel/Car/PackageDeal)",
        "start_date": "date",
        "end_date": "date",
        "total_price": "numeric(10,2)",
        "status": "string (confirmed, cancelled)",
        "created_at": "timestamp"
      },
      "relationships": {
        "trip": "Booking belongs to Trip",
        "user": "Booking belongs to User"
      }
    },
    {
      "entity": "CartItem",
      "fields": {
        "id": "UUID primary key",
        "user_id": "UUID references User",
        "type": "string (flight, hotel, car, package)",
        "reference_id": "UUID (ID of Flight/Hotel/Car/PackageDeal)",
        "details": "jsonb (selected options like seat class, room type, extras)",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "CartItem belongs to User"
      }
    },
    {
      "entity": "Favorite",
      "fields": {
        "id": "UUID primary key",
        "user_id": "UUID references User",
        "type": "string (hotel, flight, car)",
        "reference_id": "UUID",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "Favorite belongs to User"
      }
    },
    {
      "entity": "Review",
      "fields": {
        "id": "UUID primary key",
        "hotel_id": "UUID references Hotel",
        "user_id": "UUID references User",
        "rating": "integer (1-5)",
        "title": "string",
        "comment": "text",
        "created_at": "timestamp"
      },
      "relationships": {
        "hotel": "Review belongs to Hotel",
        "user": "Review belongs to User"
      }
    }
  ],
  "sample_data_requirements": {
    "flights_minimum": 50,
    "hotels_minimum": 30,
    "cars_minimum": 20,
    "test_user": {
      "email": "admin@expedia.com",
      "password": "admin123"
    }
  },
  "ui_requirements": {
    "theme": {
      "primary_color": "#1668E3",
      "accent_color": "#F8A51A",
      "background": "#FFFFFF"
    },
    "layout": {
      "responsive": true,
      "header_tabs": ["Stays", "Flights", "Cars", "Packages"],
      "use_cards": true
    },
    "interactions": {
      "date_picker": "Interactive calendar date picker for all date inputs",
      "location_autocomplete": "Autocomplete with location icons for city/airport inputs",
      "image_carousels": "Hotel photo carousels on details pages",
      "favorites": "Heart icons to toggle favorites",
      "vip_badges": "VIP Access badge styling on eligible hotels"
    }
  }
}
'})

