write_file({'path': 'design/spec.requirements.json', 'content': '{
  "project_name": "Expedia-Style Travel Booking Platform",
  "description": "A full-stack travel booking web application inspired by Expedia, allowing users to search and book flights, hotels, and car rentals, manage trips, and complete checkout with a modern responsive UI.",
  "tech_stack": {
    "frontend": "React + Vite + TailwindCSS",
    "backend": "Node.js + Express",
    "database": "PostgreSQL",
    "auth": "JWT + bcrypt"
  },
  "features": [
    {
      "name": "User Authentication & Profiles",
      "id": "auth_profiles",
      "description": "Email/password registration and login with JWT-based sessions, profile management, and booking history.",
      "priority": "P0"
    },
    {
      "name": "Flight Search & Booking",
      "id": "flights",
      "description": "Search, filter, and book flights with seat class selection and cart integration.",
      "priority": "P0"
    },
    {
      "name": "Hotel Search & Booking",
      "id": "hotels",
      "description": "Search, filter, and book hotels with room selection, amenities, and reviews display.",
      "priority": "P0"
    },
    {
      "name": "Car Rental Search & Booking",
      "id": "cars",
      "description": "Search, filter, and book rental cars with extras (GPS, child seat, insurance).",
      "priority": "P1"
    },
    {
      "name": "Package Deals",
      "id": "packages",
      "description": "Bundle flight + hotel + car into discounted packages and compare vs individual pricing.",
      "priority": "P1"
    },
    {
      "name": "Shopping Cart & Checkout",
      "id": "cart_checkout",
      "description": "Multi-item cart supporting flights, hotels, and cars with promo codes and payment form.",
      "priority": "P0"
    },
    {
      "name": "Trip Management",
      "id": "trip_management",
      "description": "View upcoming and past trips, download confirmations, and cancel or modify bookings.",
      "priority": "P1"
    }
  ],
  "pages": [
    {
      "name": "Home / Search Hub",
      "path": "/",
      "components": ["MainTabs", "SearchBarFlights", "SearchBarStays", "SearchBarCars", "SearchBarPackages", "FeaturedDealsCarousel"],
      "description": "Landing page with tabbed navigation for Stays, Flights, Cars, and Packages, including hero search bars and featured deals."
    },
    {
      "name": "Auth - Login",
      "path": "/login",
      "components": ["AuthForm", "SocialProofStrip"],
      "description": "Email/password login form with link to registration and password requirements hints."
    },
    {
      "name": "Auth - Register",
      "path": "/register",
      "components": ["AuthForm"],
      "description": "Registration form capturing name, email, password, and phone number."
    },
    {
      "name": "Flight Results",
      "path": "/flights",
      "components": ["FiltersSidebar", "SortBar", "FlightResultCard", "Pagination"],
      "description": "Search results list for flights with filters (price, airline, stops, departure time) and sorting options."
    },
    {
      "name": "Flight Details",
      "path": "/flights/:id",
      "components": ["FlightSummary", "ItineraryTimeline", "FareClassSelector", "AddToCartBar"],
      "description": "Detailed view of a selected flight including layovers, duration, and seat class selection."
    },
    {
      "name": "Hotel Results",
      "path": "/stays",
      "components": ["MapToggle", "FiltersBar", "HotelResultCard", "Pagination"],
      "description": "Hotel search results with filters (star rating, price, amenities, distance) and optional map view toggle."
    },
    {
      "name": "Hotel Details",
      "path": "/stays/:id",
      "components": ["ImageCarousel", "HotelSummary", "AmenitiesGrid", "RoomTypeList", "ReviewsSection", "MapSection", "VIPBadge"],
      "description": "Hotel detail page with photo gallery, room selection, amenities, reviews, and map."
    },
    {
      "name": "Car Results",
      "path": "/cars",
      "components": ["FiltersBar", "CarResultCard", "ExtrasSelector"],
      "description": "Car rental results with filters and extras selection (GPS, child seat, insurance)."
    },
    {
      "name": "Packages Results",
      "path": "/packages",
      "components": ["PackageResultCard", "PackageComparison"],
      "description": "Package deals combining flight + hotel (+ optional car) with comparison against individual pricing."
    },
    {
      "name": "Cart & Checkout",
      "path": "/cart",
      "components": ["CartItemList", "PriceBreakdown", "PromoCodeInput", "PaymentForm"],
      "description": "Shopping cart showing all selected items with price breakdown, promo code entry, and secure payment form."
    },
    {
      "name": "Trips Dashboard",
      "path": "/trips",
      "components": ["UpcomingTripsList", "PastTripsList", "TripCard", "DownloadConfirmationButton", "CancelModifyActions"],
      "description": "Trip management page listing upcoming and past trips with actions for viewing, downloading, and cancelling/modifying bookings."
    },
    {
      "name": "User Profile",
      "path": "/profile",
      "components": ["ProfileForm", "SavedPaymentsList", "SecuritySettings"],
      "description": "User profile management including personal info, contact details, and saved payment methods (mocked)."
    }
  ],
  "api_endpoints": [
    {"method": "POST", "path": "/auth/register", "description": "Register a new user.", "auth_required": false},
    {"method": "POST", "path": "/auth/login", "description": "Authenticate user and return JWT.", "auth_required": false},
    {"method": "GET", "path": "/auth/me", "description": "Get current authenticated user profile.", "auth_required": true},
    {"method": "PUT", "path": "/users/me", "description": "Update current user profile.", "auth_required": true},

    {"method": "GET", "path": "/flights", "description": "Search flights with query params for origin, destination, dates, passengers, filters, and sorting.", "auth_required": false},
    {"method": "GET", "path": "/flights/:id", "description": "Get details for a specific flight.", "auth_required": false},

    {"method": "GET", "path": "/hotels", "description": "Search hotels with filters and pagination.", "auth_required": false},
    {"method": "GET", "path": "/hotels/:id", "description": "Get hotel details including rooms, amenities, and reviews.", "auth_required": false},

    {"method": "GET", "path": "/cars", "description": "Search rental cars with filters.", "auth_required": false},
    {"method": "GET", "path": "/cars/:id", "description": "Get rental car details.", "auth_required": false},

    {"method": "GET", "path": "/packages", "description": "Get package deals and pricing comparison.", "auth_required": false},

    {"method": "GET", "path": "/cart", "description": "Get current user's cart items.", "auth_required": true},
    {"method": "POST", "path": "/cart/items", "description": "Add an item (flight, hotel, car) to cart.", "auth_required": true},
    {"method": "PUT", "path": "/cart/items/:id", "description": "Update a cart item (e.g., quantity, seat class, room type).", "auth_required": true},
    {"method": "DELETE", "path": "/cart/items/:id", "description": "Remove item from cart.", "auth_required": true},

    {"method": "POST", "path": "/checkout", "description": "Perform checkout for current cart, applying promo codes and creating bookings.", "auth_required": true},

    {"method": "GET", "path": "/trips", "description": "List upcoming and past trips (bookings) for current user.", "auth_required": true},
    {"method": "GET", "path": "/trips/:id", "description": "Get details for a specific trip/booking.", "auth_required": true},
    {"method": "POST", "path": "/trips/:id/cancel", "description": "Cancel a specific booking (subject to simple rules).", "auth_required": true},

    {"method": "GET", "path": "/meta/locations", "description": "Autocomplete suggestions for locations based on query.", "auth_required": false},
    {"method": "GET", "path": "/meta/amenities", "description": "List of hotel amenities.", "auth_required": false}
  ],
  "data_models": [
    {
      "entity": "User",
      "fields": {
        "id": "UUID primary key",
        "name": "string",
        "email": "string unique",
        "password_hash": "string (bcrypt)",
        "phone": "string nullable",
        "created_at": "timestamp",
        "updated_at": "timestamp"
      },
      "relationships": {
        "bookings": "One-to-many with Booking",
        "cart_items": "One-to-many with CartItem"
      }
    },
    {
      "entity": "Flight",
      "fields": {
        "id": "UUID primary key",
        "airline": "string",
        "flight_number": "string",
        "origin": "string (airport code)",
        "destination": "string (airport code)",
        "departure_time": "timestamp",
        "arrival_time": "timestamp",
        "duration_minutes": "integer",
        "stops": "integer",
        "price_economy": "integer (cents)",
        "price_business": "integer (cents)",
        "price_first": "integer (cents)"
      },
      "relationships": {}
    },
    {
      "entity": "Hotel",
      "fields": {
        "id": "UUID primary key",
        "name": "string",
        "location": "string (city/area)",
        "star_rating": "numeric",
        "base_price_per_night": "integer (cents)",
        "description": "text",
        "distance_center_km": "numeric",
        "vip_access": "boolean",
        "thumbnail_url": "string"
      },
      "relationships": {
        "rooms": "One-to-many with HotelRoom",
        "reviews": "One-to-many with HotelReview"
      }
    },
    {
      "entity": "HotelRoom",
      "fields": {
        "id": "UUID primary key",
        "hotel_id": "UUID (FK -> Hotel)",
        "name": "string",
        "bed_config": "string",
        "max_guests": "integer",
        "price_per_night": "integer (cents)",
        "is_refundable": "boolean"
      },
      "relationships": {}
    },
    {
      "entity": "HotelReview",
      "fields": {
        "id": "UUID primary key",
        "hotel_id": "UUID (FK -> Hotel)",
        "user_name": "string",
        "rating": "integer (1-5)",
        "comment": "text",
        "created_at": "timestamp"
      },
      "relationships": {}
    },
    {
      "entity": "Car",
      "fields": {
        "id": "UUID primary key",
        "company": "string",
        "model": "string",
        "car_type": "string",
        "seats": "integer",
        "transmission": "string",
        "fuel_type": "string",
        "price_per_day": "integer (cents)"
      },
      "relationships": {}
    },
    {
      "entity": "PackageDeal",
      "fields": {
        "id": "UUID primary key",
        "flight_id": "UUID (FK -> Flight)",
        "hotel_id": "UUID (FK -> Hotel)",
        "car_id": "UUID (FK -> Car, nullable)",
        "package_price": "integer (cents)",
        "savings_amount": "integer (cents)"
      },
      "relationships": {
        "flight": "Many-to-one with Flight",
        "hotel": "Many-to-one with Hotel",
        "car": "Many-to-one with Car (optional)"
      }
    },
    {
      "entity": "CartItem",
      "fields": {
        "id": "UUID primary key",
        "user_id": "UUID (FK -> User)",
        "item_type": "enum('flight','hotel','car','package')",
        "item_id": "UUID (ID of referenced entity)",
        "metadata": "JSONB (seat class, room type, dates, passengers, extras)",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "Many-to-one with User"
      }
    },
    {
      "entity": "Booking",
      "fields": {
        "id": "UUID primary key",
        "user_id": "UUID (FK -> User)",
        "booking_type": "enum('flight','hotel','car','package')",
        "reference_code": "string unique",
        "items": "JSONB (snapshot of booked items/prices)",
        "total_amount": "integer (cents)",
        "status": "enum('upcoming','completed','cancelled')",
        "start_date": "date",
        "end_date": "date",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "Many-to-one with User"
      }
    },
    {
      "entity": "Location",
      "fields": {
        "id": "UUID primary key",
        "type": "enum('city','airport')",
        "code": "string",
        "name": "string",
        "country": "string"
      },
      "relationships": {}
    },
    {
      "entity": "PromoCode",
      "fields": {
        "id": "UUID primary key",
        "code": "string unique",
        "description": "string",
        "discount_type": "enum('percent','fixed')",
        "discount_value": "integer",
        "max_uses": "integer nullable",
        "expires_at": "timestamp nullable"
      },
      "relationships": {}
    }
  ],
  "ui_style": {
    "theme_colors": {
      "primary": "#1668E3",
      "accent": "#FBBF24",
      "background": "#FFFFFF",
      "surface": "#F3F4F6"
    },
    "layout": {
      "responsive": true,
      "header": "Top navigation with logo, main tabs (Stays, Flights, Cars, Packages), auth buttons, and profile menu.",
      "cards": "Use rounded white cards with subtle shadows for results and content blocks."
    },
    "components": {
      "date_picker": "Interactive calendar-based date picker supporting single and range selection.",
      "location_autocomplete": "Text input with dropdown suggestions and location/airport icons.",
      "image_carousel": "Horizontal image slider for hotel photos.",
      "tabs": "Prominent tab navigation for product types.",
      "icons": ["heart favorites", "VIP Access badge"]
    }
  },
  "sample_data_requirements": {
    "flights_min": 50,
    "hotels_min": 30,
    "cars_min": 20,
    "test_user": {
      "email": "admin@expedia.com",
      "password": "admin123"
    }
  }
}
'})

