write_file({'path': 'design/spec.requirements.json', 'content': '{
  "project_name": "Expedia-Style Travel Booking Platform",
  "description": "A full-featured travel booking platform for flights, hotels, car rentals, and package deals with Expedia-inspired UI, JWT authentication, and PostgreSQL-backed data.",
  "tech_stack": {
    "frontend": "React + Vite + TailwindCSS",
    "backend": "Node.js + Express",
    "database": "PostgreSQL",
    "auth": "JWT + bcrypt"
  },
  "features": [
    {
      "name": "User Authentication",
      "id": "auth-core",
      "description": "Email/password registration and login with JWT-based authentication and secure password hashing.",
      "priority": "P0"
    },
    {
      "name": "User Profile Management",
      "id": "user-profile",
      "description": "Manage profile details (name, email, phone) and view booking history and saved payment methods (stored as masked tokens, not raw card data).",
      "priority": "P0"
    },
    {
      "name": "Flight Search & Booking",
      "id": "flights-core",
      "description": "Search, filter, sort, and book flights with seat class selection and add to cart/checkout.",
      "priority": "P0"
    },
    {
      "name": "Hotel Search & Booking",
      "id": "hotels-core",
      "description": "Search, filter, and book hotels with room type selection, photo galleries, and reviews display.",
      "priority": "P0"
    },
    {
      "name": "Car Rental",
      "id": "cars-core",
      "description": "Search and book rental cars with filters and optional extras (GPS, child seat, insurance).",
      "priority": "P1"
    },
    {
      "name": "Package Deals",
      "id": "packages-core",
      "description": "Create bundled offers combining flight + hotel + car with discounted pricing and comparison vs individual items.",
      "priority": "P1"
    },
    {
      "name": "Shopping Cart & Checkout",
      "id": "cart-checkout",
      "description": "Multi-item cart supporting flights, hotels, and cars with price breakdown, promo codes, and payment form.",
      "priority": "P0"
    },
    {
      "name": "Trip Management",
      "id": "trip-management",
      "description": "View upcoming and past trips, download confirmations, and cancel or modify bookings (subject to policy).",
      "priority": "P1"
    },
    {
      "name": "Favorites & VIP Access",
      "id": "favorites-vip",
      "description": "Allow users to favorite listings (heart icon) and highlight VIP Access properties with badges.",
      "priority": "P2"
    }
  ],
  "pages": [
    {
      "name": "Landing/Home",
      "path": "/",
      "components": ["TopNavTabs", "HeroSearchPanel", "DatePicker", "LocationAutocomplete", "FeaturedDealsCarousel"],
      "description": "Expedia-style home page with tabs for Stays, Flights, Cars, and Packages and a hero search panel."
    },
    {
      "name": "Auth - Login",
      "path": "/login",
      "components": ["AuthForm", "EmailInput", "PasswordInput"],
      "description": "Login form with email/password and link to registration."
    },
    {
      "name": "Auth - Register",
      "path": "/register",
      "components": ["AuthForm", "EmailInput", "PasswordInput", "NameInput"],
      "description": "Registration form for new users."
    },
    {
      "name": "Flights Search Results",
      "path": "/flights",
      "components": ["SearchFilters", "SortBar", "FlightCard", "Pagination"],
      "description": "List of available flights with filters and sorting. Search parameters passed via query string."
    },
    {
      "name": "Flight Details",
      "path": "/flights/:id",
      "components": ["FlightSummary", "FareClassSelector", "ItineraryTimeline", "AddToCartButton"],
      "description": "Detailed view of a specific flight with seat class selection and booking CTA."
    },
    {
      "name": "Hotels Search Results",
      "path": "/hotels",
      "components": ["SearchFilters", "SortBar", "HotelCard", "MapToggle", "Pagination"],
      "description": "Hotel search results with filters, map view toggle, and VIP/favorites indicators."
    },
    {
      "name": "Hotel Details",
      "path": "/hotels/:id",
      "components": ["ImageCarousel", "HotelSummary", "AmenitiesList", "RoomTypeSelector", "ReviewsList", "MapEmbed", "AddToCartButton"],
      "description": "Hotel detail page with photos, amenities, room selection, reviews, and map."
    },
    {
      "name": "Cars Search Results",
      "path": "/cars",
      "components": ["SearchFilters", "CarCard", "ExtrasSelector", "Pagination"],
      "description": "Car rental search results with filters and extras selection."
    },
    {
      "name": "Packages",
      "path": "/packages",
      "components": ["PackageSearchForm", "PackageCard", "ComparePricingPanel"],
      "description": "Search and view bundled package deals combining flights, hotels, and cars."
    },
    {
      "name": "Shopping Cart",
      "path": "/cart",
      "components": ["CartItemList", "PriceBreakdown", "PromoCodeInput", "CheckoutButton"],
      "description": "Multi-item cart summarizing selected flights, hotels, and cars with pricing and promo code."
    },
    {
      "name": "Checkout",
      "path": "/checkout",
      "components": ["TravelerDetailsForm", "PaymentForm", "OrderSummary"],
      "description": "Secure checkout page with traveler details and payment form."
    },
    {
      "name": "User Profile",
      "path": "/account/profile",
      "components": ["ProfileForm", "SavedPaymentsList"],
      "description": "User profile management for name, email, phone, and saved payment method tokens."
    },
    {
      "name": "Trips",
      "path": "/account/trips",
      "components": ["TripsList", "TripCard", "DownloadConfirmationButton", "CancelModifyButtons"],
      "description": "List of upcoming and past trips across flights, hotels, and cars."
    }
  ],
  "api_endpoints": [
    {"method": "POST", "path": "/auth/register", "description": "Register a new user", "auth_required": false},
    {"method": "POST", "path": "/auth/login", "description": "Login and receive JWT", "auth_required": false},
    {"method": "GET", "path": "/auth/me", "description": "Get current user profile", "auth_required": true},
    {"method": "PUT", "path": "/users/me", "description": "Update current user profile", "auth_required": true},
    {"method": "GET", "path": "/users/me/bookings", "description": "Get booking history for current user", "auth_required": true},

    {"method": "GET", "path": "/flights", "description": "Search flights with filters and sorting", "auth_required": false},
    {"method": "GET", "path": "/flights/:id", "description": "Get single flight details", "auth_required": false},
    {"method": "POST", "path": "/flights/:id/book", "description": "Book a flight (creates booking and cart item or direct booking)", "auth_required": true},

    {"method": "GET", "path": "/hotels", "description": "Search hotels with filters and sorting", "auth_required": false},
    {"method": "GET", "path": "/hotels/:id", "description": "Get hotel details including rooms and reviews", "auth_required": false},
    {"method": "POST", "path": "/hotels/:id/book", "description": "Book a hotel room", "auth_required": true},

    {"method": "GET", "path": "/cars", "description": "Search rental cars", "auth_required": false},
    {"method": "GET", "path": "/cars/:id", "description": "Get car details", "auth_required": false},
    {"method": "POST", "path": "/cars/:id/book", "description": "Book a rental car with extras", "auth_required": true},

    {"method": "POST", "path": "/packages/search", "description": "Search package deals combining flights, hotels, and cars", "auth_required": false},

    {"method": "GET", "path": "/cart", "description": "Get current user's cart items", "auth_required": true},
    {"method": "POST", "path": "/cart/items", "description": "Add an item (flight/hotel/car) to cart", "auth_required": true},
    {"method": "PUT", "path": "/cart/items/:id", "description": "Update a cart item (e.g., quantity, extras)", "auth_required": true},
    {"method": "DELETE", "path": "/cart/items/:id", "description": "Remove an item from cart", "auth_required": true},

    {"method": "POST", "path": "/checkout", "description": "Perform checkout for current cart, apply promo codes, create bookings", "auth_required": true},

    {"method": "GET", "path": "/trips", "description": "List upcoming and past trips for current user", "auth_required": true},
    {"method": "GET", "path": "/trips/:id", "description": "Get details for a specific trip", "auth_required": true},
    {"method": "POST", "path": "/trips/:id/cancel", "description": "Cancel a trip or component booking (where allowed)", "auth_required": true},

    {"method": "POST", "path": "/favorites", "description": "Add an entity (flight/hotel/car) to favorites", "auth_required": true},
    {"method": "DELETE", "path": "/favorites/:id", "description": "Remove a favorite", "auth_required": true},
    {"method": "GET", "path": "/favorites", "description": "List favorites for current user", "auth_required": true}
  ],
  "data_models": [
    {
      "entity": "User",
      "fields": {
        "id": "uuid primary key",
        "email": "string unique not null",
        "password_hash": "string not null",
        "name": "string",
        "phone": "string",
        "created_at": "timestamp",
        "updated_at": "timestamp"
      },
      "relationships": {
        "bookings": "One User has many Bookings",
        "favorites": "One User has many Favorites",
        "payment_methods": "One User has many PaymentMethods"
      }
    },
    {
      "entity": "PaymentMethod",
      "fields": {
        "id": "uuid primary key",
        "user_id": "uuid references User(id)",
        "provider": "string (e.g., 'visa', 'mastercard')",
        "last4": "string(4)",
        "token": "string (mock token, not real PAN)",
        "expiry_month": "integer",
        "expiry_year": "integer",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "Many PaymentMethods belong to one User"
      }
    },
    {
      "entity": "Flight",
      "fields": {
        "id": "uuid primary key",
        "airline": "string",
        "flight_number": "string",
        "origin": "string (airport code)",
        "destination": "string (airport code)",
        "depart_at": "timestamp",
        "arrive_at": "timestamp",
        "duration_minutes": "integer",
        "stops": "integer",
        "base_price_cents": "integer",
        "seat_classes": "jsonb (e.g., {economy: 10000, business: 25000})"
      },
      "relationships": {
        "bookings": "One Flight has many FlightBookings"
      }
    },
    {
      "entity": "Hotel",
      "fields": {
        "id": "uuid primary key",
        "name": "string",
        "location": "string (city/area)",
        "latitude": "numeric",
        "longitude": "numeric",
        "star_rating": "numeric",
        "description": "text",
        "amenities": "jsonb (array of strings)",
        "is_vip_access": "boolean",
        "thumbnail_url": "string"
      },
      "relationships": {
        "rooms": "One Hotel has many HotelRoomTypes",
        "reviews": "One Hotel has many HotelReviews"
      }
    },
    {
      "entity": "HotelRoomType",
      "fields": {
        "id": "uuid primary key",
        "hotel_id": "uuid references Hotel(id)",
        "name": "string",
        "bed_config": "string",
        "max_guests": "integer",
        "price_per_night_cents": "integer",
        "is_refundable": "boolean"
      },
      "relationships": {
        "hotel": "Many HotelRoomTypes belong to one Hotel"
      }
    },
    {
      "entity": "HotelReview",
      "fields": {
        "id": "uuid primary key",
        "hotel_id": "uuid references Hotel(id)",
        "user_name": "string (display only)",
        "rating": "integer",
        "comment": "text",
        "created_at": "timestamp"
      },
      "relationships": {
        "hotel": "Many HotelReviews belong to one Hotel"
      }
    },
    {
      "entity": "Car",
      "fields": {
        "id": "uuid primary key",
        "company": "string",
        "model": "string",
        "car_type": "string (e.g., economy, suv)",
        "seats": "integer",
        "transmission": "string",
        "fuel_type": "string",
        "base_price_per_day_cents": "integer"
      },
      "relationships": {
        "bookings": "One Car has many CarBookings"
      }
    },
    {
      "entity": "CartItem",
      "fields": {
        "id": "uuid primary key",
        "user_id": "uuid references User(id)",
        "item_type": "enum('flight','hotel','car','package')",
        "item_id": "uuid (references specific entity id)",
        "meta": "jsonb (seat class, dates, guests, extras, etc.)",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "Many CartItems belong to one User"
      }
    },
    {
      "entity": "Booking",
      "fields": {
        "id": "uuid primary key",
        "user_id": "uuid references User(id)",
        "booking_type": "enum('flight','hotel','car','package')",
        "status": "enum('upcoming','completed','cancelled')",
        "total_price_cents": "integer",
        "currency": "string",
        "created_at": "timestamp",
        "start_date": "date",
        "end_date": "date"
      },
      "relationships": {
        "user": "Many Bookings belong to one User",
        "flight_booking": "One Booking may have one FlightBooking",
        "hotel_booking": "One Booking may have one HotelBooking",
        "car_booking": "One Booking may have one CarBooking"
      }
    },
    {
      "entity": "FlightBooking",
      "fields": {
        "id": "uuid primary key",
        "booking_id": "uuid references Booking(id)",
        "flight_id": "uuid references Flight(id)",
        "seat_class": "string",
        "passengers": "jsonb (list of passenger names)"
      },
      "relationships": {
        "booking": "One FlightBooking belongs to one Booking",
        "flight": "One FlightBooking belongs to one Flight"
      }
    },
    {
      "entity": "HotelBooking",
      "fields": {
        "id": "uuid primary key",
        "booking_id": "uuid references Booking(id)",
        "hotel_id": "uuid references Hotel(id)",
        "room_type_id": "uuid references HotelRoomType(id)",
        "guests": "integer",
        "rooms": "integer"
      },
      "relationships": {
        "booking": "One HotelBooking belongs to one Booking",
        "hotel": "One HotelBooking belongs to one Hotel",
        "room_type": "One HotelBooking belongs to one HotelRoomType"
      }
    },
    {
      "entity": "CarBooking",
      "fields": {
        "id": "uuid primary key",
        "booking_id": "uuid references Booking(id)",
        "car_id": "uuid references Car(id)",
        "pickup_location": "string",
        "dropoff_location": "string",
        "extras": "jsonb (gps, child seat, insurance)",
        "days": "integer"
      },
      "relationships": {
        "booking": "One CarBooking belongs to one Booking",
        "car": "One CarBooking belongs to one Car"
      }
    },
    {
      "entity": "Favorite",
      "fields": {
        "id": "uuid primary key",
        "user_id": "uuid references User(id)",
        "item_type": "enum('flight','hotel','car')",
        "item_id": "uuid",
        "created_at": "timestamp"
      },
      "relationships": {
        "user": "Many Favorites belong to one User"
      }
    },
    {
      "entity": "PromoCode",
      "fields": {
        "id": "uuid primary key",
        "code": "string unique",
        "description": "string",
        "discount_type": "enum('percentage','fixed')",
        "discount_value": "numeric",
        "valid_from": "date",
        "valid_to": "date",
        "max_uses": "integer",
        "used_count": "integer"
      },
      "relationships": {}
    }
  ],
  "sample_data_requirements": {
    "flights_min": 50,
    "hotels_min": 30,
    "cars_min": 20,
    "test_user": {
      "email": "admin@expedia.com",
      "password": "admin123"
    }
  },
  "ui_requirements": {
    "style_reference": "Modern Expedia-inspired design",
    "primary_color": "#1668E3",
    "accent_color": "#F9A826",
    "background": "#FFFFFF",
    "layout": "Responsive with cards and tab navigation for Stays/Flights/Cars/Packages",
    "components": [
      "Interactive date picker with calendar",
      "Autocomplete for locations with icons",
      "Image carousels for hotels",
      "Tab navigation for Stays/Flights/Cars/Packages",
      "Heart icons for favorites",
      "VIP Access badges"
    ]
  },
  "non_functional_requirements": {
    "performance": "Search endpoints should support basic pagination and respond within a reasonable time for sample dataset.",
    "security": "Passwords must be hashed with bcrypt; JWTs used for authenticated endpoints; never store raw card numbers.",
    "urls": {
      "frontend": "http://localhost:8003",
      "backend": "http://localhost:8002"
    }
  }
}
'})

