write_file({'path': 'design/spec.requirements.json', 'content': '{
  "project_name": "Expedia-Style Travel Booking Platform",
  "description": "A full-featured Expedia-inspired travel booking web app supporting flights, hotels, cars, packages, cart/checkout, and trip management with JWT authentication.",
  "environments": {
    "frontend_url": "http://localhost:8002",
    "backend_api_url": "http://localhost:3001"
  },
  "branding": {
    "primary_color": "#1668E3",
    "accent_color": "#F5A623",
    "background": "#FFFFFF",
    "style": "Modern, clean card-based UI inspired by Expedia"
  },
  "tech_stack": {
    "frontend": {
      "framework": "React",
      "build_tool": "Vite",
      "styling": "TailwindCSS"
    },
    "backend": {
      "runtime": "Node.js",
      "framework": "Express"
    },
    "database": "PostgreSQL",
    "auth": {
      "type": "JWT",
      "password_hash": "bcrypt"
    }
  },
  "seed_data": {
    "minimums": {
      "flights": 50,
      "hotels": 30,
      "cars": 20
    },
    "test_user": {
      "email": "admin@expedia.com",
      "password": "admin123"
    }
  },
  "features": [
    {
      "name": "Authentication",
      "priority": "P0",
      "description": "Email/password registration and login; JWT session; profile management; booking history."
    },
    {
      "name": "Flight Search & Booking",
      "priority": "P0",
      "description": "Search one-way/round-trip flights; filter/sort; view details; choose seat class; add to cart; checkout."
    },
    {
      "name": "Hotel Search & Booking",
      "priority": "P0",
      "description": "Search hotels; filter; hotel detail with gallery/amenities/rooms; reviews; map view; add room booking to cart."
    },
    {
      "name": "Car Rental",
      "priority": "P1",
      "description": "Search cars; filter; car detail; add extras; add to cart."
    },
    {
      "name": "Packages",
      "priority": "P1",
      "description": "Bundle flight+hotel+car; show discount and comparison vs individual pricing."
    },
    {
      "name": "Cart & Checkout",
      "priority": "P0",
      "description": "Multi-item cart; price breakdown (taxes/fees); promo code; payment form; place booking(s)."
    },
    {
      "name": "Trip Management",
      "priority": "P0",
      "description": "View upcoming/past trips; download confirmation; cancel/modify bookings (basic)."
    },
    {
      "name": "Favorites & Badges",
      "priority": "P2",
      "description": "Heart favorites; VIP Access badges on select hotels."
    }
  ],
  "pages": [
    {
      "name": "Home",
      "path": "/",
      "components": ["TopNav", "SearchTabs (Stays/Flights/Cars/Packages)", "Hero", "RecentSearches"],
      "description": "Landing page with tabbed search forms and Expedia-like header."
    },
    {
      "name": "Auth - Login",
      "path": "/login",
      "components": ["LoginForm"],
      "description": "Login with email/password; store JWT."
    },
    {
      "name": "Auth - Register",
      "path": "/register",
      "components": ["RegisterForm"],
      "description": "Register new user with email/password/name."
    },
    {
      "name": "Profile",
      "path": "/account/profile",
      "components": ["ProfileForm", "SavedPayments"],
      "description": "Manage name/email/phone and saved payment methods."
    },
    {
      "name": "Trips",
      "path": "/trips",
      "components": ["UpcomingTrips", "PastTrips", "TripCard"],
      "description": "View bookings grouped by upcoming/past; cancel/modify; download confirmation."
    },
    {
      "name": "Flights Search Results",
      "path": "/flights",
      "components": ["FlightSearchBar", "Filters", "Sort", "FlightResultCard"],
      "description": "Search results with filtering and sorting."
    },
    {
      "name": "Flight Details",
      "path": "/flights/:id",
      "components": ["FlightDetails", "SeatClassSelector", "AddToCart"],
      "description": "Detailed flight view including layovers; add to cart with seat class."
    },
    {
      "name": "Hotels Search Results",
      "path": "/hotels",
      "components": ["HotelSearchBar", "Filters", "MapViewToggle", "HotelCard"],
      "description": "Hotel results with filters and optional map view."
    },
    {
      "name": "Hotel Details",
      "path": "/hotels/:id",
      "components": ["ImageCarousel", "Amenities", "RoomTypeSelector", "Reviews", "AddToCart"],
      "description": "Hotel details with gallery, amenities, rooms, reviews, and booking."
    },
    {
      "name": "Cars Search Results",
      "path": "/cars",
      "components": ["CarSearchBar", "Filters", "CarCard"],
      "description": "Car results with filters; add to cart with extras."
    },
    {
      "name": "Packages",
      "path": "/packages",
      "components": ["PackageSearch", "PackageCard", "PriceComparison"],
      "description": "Bundle builder and package results with discount and comparison."
    },
    {
      "name": "Cart",
      "path": "/cart",
      "components": ["CartItemList", "PriceBreakdown", "PromoCode"],
      "description": "View/edit multi-item cart; apply promo code."
    },
    {
      "name": "Checkout",
      "path": "/checkout",
      "components": ["TravelerDetails", "PaymentForm", "OrderSummary"],
      "description": "Checkout flow; place booking(s)."
    }
  ],
  "api_endpoints": [
    {"method": "GET", "path": "/health", "description": "Health check", "auth_required": false},

    {"method": "POST", "path": "/auth/register", "description": "Register user", "auth_required": false},
    {"method": "POST", "path": "/auth/login", "description": "Login user; return JWT", "auth_required": false},
    {"method": "GET", "path": "/auth/me", "description": "Get current user profile", "auth_required": true},
    {"method": "PUT", "path": "/auth/me", "description": "Update current user profile", "auth_required": true},

    {"method": "GET", "path": "/flights", "description": "Search flights with query params (origin,destination,departDate,returnDate,passengers,tripType,filters,sort)", "auth_required": false},
    {"method": "GET", "path": "/flights/:id", "description": "Flight details", "auth_required": false},

    {"method": "GET", "path": "/hotels", "description": "Search hotels with query params (location,checkIn,checkOut,guests,rooms,filters,sort)", "auth_required": false},
    {"method": "GET", "path": "/hotels/:id", "description": "Hotel details incl rooms/photos/amenities/reviews", "auth_required": false},

    {"method": "GET", "path": "/cars", "description": "Search cars with query params (pickup,dropoff,pickupDate,dropoffDate,filters,sort)", "auth_required": false},
    {"method": "GET", "path": "/cars/:id", "description": "Car details", "auth_required": false},

    {"method": "POST", "path": "/cart/items", "description": "Add item to cart (flight/hotel/car/package)", "auth_required": true},
    {"method": "GET", "path": "/cart", "description": "Get current cart", "auth_required": true},
    {"method": "PUT", "path": "/cart/items/:id", "description": "Update cart item (quantities, seat class, extras)", "auth_required": true},
    {"method": "DELETE", "path": "/cart/items/:id", "description": "Remove cart item", "auth_required": true},
    {"method": "POST", "path": "/cart/apply-promo", "description": "Apply promo code", "auth_required": true},

    {"method": "POST", "path": "/checkout", "description": "Checkout cart; create booking(s); clear cart", "auth_required": true},

    {"method": "GET", "path": "/bookings", "description": "List user bookings (history/upcoming)", "auth_required": true},
    {"method": "GET", "path": "/bookings/:id", "description": "Get booking details; confirmation payload", "auth_required": true},
    {"method": "POST", "path": "/bookings/:id/cancel", "description": "Cancel booking", "auth_required": true},
    {"method": "POST", "path": "/bookings/:id/modify", "description": "Modify booking (basic: dates/extras)", "auth_required": true}
  ],
  "data_models": [
    {
      "entity": "users",
      "fields": {
        "id": "uuid",
        "email": "text unique",
        "password_hash": "text",
        "full_name": "text",
        "phone": "text",
        "created_at": "timestamptz"
      },
      "relationships": {
        "has_many": ["payment_methods", "bookings", "cart_items", "favorites"]
      }
    },
    {
      "entity": "payment_methods",
      "fields": {
        "id": "uuid",
        "user_id": "uuid fk users",
        "brand": "text",
        "last4": "text",
        "exp_month": "int",
        "exp_year": "int",
        "billing_zip": "text",
        "created_at": "timestamptz"
      }
    },
    {
      "entity": "flights",
      "fields": {
        "id": "uuid",
        "airline": "text",
        "flight_number": "text",
        "origin": "text",
        "destination": "text",
        "depart_at": "timestamptz",
        "arrive_at": "timestamptz",
        "duration_minutes": "int",
        "stops": "int",
        "layover_airports": "text[]",
        "base_price_cents": "int",
        "created_at": "timestamptz"
      }
    },
    {
      "entity": "hotels",
      "fields": {
        "id": "uuid",
        "name": "text",
        "location": "text",
        "address": "text",
        "lat": "numeric",
        "lng": "numeric",
        "star_rating": "numeric",
        "nightly_price_cents": "int",
        "amenities": "text[]",
        "vip_access": "boolean",
        "description": "text",
        "created_at": "timestamptz"
      },
      "relationships": {
        "has_many": ["hotel_photos", "hotel_rooms", "hotel_reviews"]
      }
    },
    {
      "entity": "hotel_rooms",
      "fields": {
        "id": "uuid",
        "hotel_id": "uuid fk hotels",
        "room_type": "text",
        "bed_configuration": "text",
        "max_guests": "int",
        "price_per_night_cents": "int",
        "refundable": "boolean"
      }
    },
    {
      "entity": "hotel_photos",
      "fields": {
        "id": "uuid",
        "hotel_id": "uuid fk hotels",
        "url": "text",
        "sort_order": "int"
      }
    },
    {
      "entity": "hotel_reviews",
      "fields": {
        "id": "uuid",
        "hotel_id": "uuid fk hotels",
        "author_name": "text",
        "rating": "numeric",
        "title": "text",
        "body": "text",
        "created_at": "timestamptz"
      }
    },
    {
      "entity": "cars",
      "fields": {
        "id": "uuid",
        "company": "text",
        "model": "text",
        "car_type": "text",
        "seats": "int",
        "transmission": "text",
        "fuel_type": "text",
        "base_price_per_day_cents": "int",
        "created_at": "timestamptz"
      }
    },
    {
      "entity": "car_extras",
      "fields": {
        "id": "uuid",
        "code": "text",
        "name": "text",
        "price_cents": "int"
      }
    },
    {
      "entity": "packages",
      "fields": {
        "id": "uuid",
        "flight_id": "uuid fk flights",
        "hotel_id": "uuid fk hotels",
        "car_id": "uuid fk cars",
        "discount_cents": "int",
        "created_at": "timestamptz"
      }
    },
    {
      "entity": "cart_items",
      "fields": {
        "id": "uuid",
        "user_id": "uuid fk users",
        "item_type": "text enum: flight|hotel|car|package",
        "item_id": "uuid",
        "start_date": "date",
        "end_date": "date",
        "quantity": "int",
        "options": "jsonb (seat_class, extras, guests, rooms)",
        "price_cents": "int",
        "created_at": "timestamptz"
      }
    },
    {
      "entity": "bookings",
      "fields": {
        "id": "uuid",
        "user_id": "uuid fk users",
        "status": "text enum: confirmed|cancelled|modified",
        "total_cents": "int",
        "tax_cents": "int",
        "fees_cents": "int",
        "promo_code": "text",
        "promo_discount_cents": "int",
        "confirmation_number": "text",
        "created_at": "timestamptz"
      },
      "relationships": {
        "has_many": ["booking_items"]
      }
    },
    {
      "entity": "booking_items",
      "fields": {
        "id": "uuid",
        "booking_id": "uuid fk bookings",
        "item_type": "text",
        "item_id": "uuid",
        "start_date": "date",
        "end_date": "date",
        "quantity": "int",
        "options": "jsonb",
        "price_cents": "int"
      }
    },
    {
      "entity": "favorites",
      "fields": {
        "id": "uuid",
        "user_id": "uuid fk users",
        "entity_type": "text enum: hotel|flight|car",
        "entity_id": "uuid",
        "created_at": "timestamptz"
      }
    }
  ],
  "non_functional": {
    "responsive": true,
    "accessibility": "Basic (labels, focus states)",
    "security": ["bcrypt password hashing", "JWT Bearer tokens", "server-side validation"],
    "performance": ["server-side filtering/sorting", "pagination optional"],
    "notes": [
      "Map view can be implemented as a simplified map placeholder using coordinates (no paid API required).",
      "Payment form is simulated; do not store full card numbers; store only brand/last4/exp."
    ]
  }
}
'})
