write_file({'path': 'design/spec.requirements.json', 'content': '{
  "project": {
    "name": "FoodHub",
    "type": "food_delivery_platform",
    "inspiration": ["DoorDash", "Uber Eats"],
    "primary_color": "#FF3008",
    "testing_urls": {
      "frontend": "http://localhost:8000",
      "backend_api": "http://localhost:3000"
    }
  },
  "design_references": {
    "must_match_style": true,
    "reference_images": [
      {
        "path": "screenshot/doordash/home.png",
        "notes": "Homepage layout with left sidebar nav, category pills, promo banners, restaurant cards"
      },
      {
        "path": "screenshot/doordash/item_detail.png",
        "notes": "Store page with circular category icons, promo banner, product grid"
      },
      {
        "path": "screenshot/doordash/goods.png",
        "notes": "Product detail modal with large image, price, quantity selector, add to cart"
      }
    ],
    "ui_requirements": {
      "layout": {
        "left_sidebar_nav": {
          "visible_on_desktop": true,
          "items": [
            { "key": "home", "label": "Home", "icon": "home" },
            { "key": "grocery", "label": "Grocery", "icon": "shopping-bag" },
            { "key": "retail", "label": "Retail", "icon": "store" },
            { "key": "convenience", "label": "Convenience", "icon": "sparkles" },
            { "key": "pharmacy", "label": "Pharmacy", "icon": "plus" },
            { "key": "profile", "label": "Profile", "icon": "user" }
          ]
        },
        "top_header": {
          "elements": [
            "logo",
            "search_bar",
            "location_selector",
            "delivery_pickup_toggle",
            "cart_icon_with_badge"
          ]
        },
        "bottom_sticky_bar": {
          "purpose": "minimum order prompts and quick cart access",
          "enabled": true
        }
      },
      "components": {
        "category_pills": {
          "style": "rounded pills/tags",
          "shows_emoji": true,
          "quick_filters": true
        },
        "promo_banners": {
          "style": "cards with gradient backgrounds",
          "placement": "homepage and store page"
        },
        "restaurant_cards": {
          "fields": [
            "hero_image",
            "name",
            "rating_stars",
            "distance",
            "delivery_time",
            "delivery_fee",
            "price_range"
          ]
        },
        "store_page_category_icons": {
          "style": "circular icons with label",
          "scrollable": true
        },
        "product_cards": {
          "fields": ["image", "name", "unit_size", "price"],
          "affordances": ["plus_button_overlay_to_open_modal_or_add"],
          "price_format": "dollar sign superscript"
        },
        "product_detail_modal": {
          "fields": [
            "close_button",
            "large_image",
            "title",
            "price",
            "unit_info",
            "stock_badge",
            "quantity_stepper",
            "customizations",
            "special_instructions"
          ],
          "primary_cta": {
            "label": "Add to cart",
            "color": "#FF3008"
          },
          "recommendations": "You may also like carousel"
        }
      }
    }
  },
  "tech_stack": {
    "frontend": {
      "framework": "React",
      "build_tool": "Vite",
      "styling": "TailwindCSS"
    },
    "backend": {
      "runtime": "Node.js",
      "framework": "Express"
    },
    "database": {
      "engine": "PostgreSQL"
    },
    "auth": {
      "type": "JWT",
      "password_hash": "bcrypt"
    }
  },
  "features": {
    "authentication": {
      "email_password": true,
      "jwt": true,
      "profile": true,
      "delivery_addresses": true,
      "payment_methods": true
    },
    "restaurant_browsing": {
      "browse_by_category": true,
      "homepage_featured": true,
      "filters": ["rating", "delivery_time", "price_range", "distance"],
      "sort": ["rating", "delivery_time", "distance"],
      "card_fields": ["image", "name", "rating", "delivery_time", "price_range", "delivery_fee", "distance"]
    },
    "search": {
      "search_restaurants": true,
      "search_dishes": true,
      "suggestions": true,
      "recent_searches": true,
      "category_quick_filters": true
    },
    "restaurant_page": {
      "header_cover_image": true,
      "info_fields": ["rating", "reviews_count", "delivery_time", "delivery_fee", "minimum_order"],
      "menu_categories": true,
      "menu_items": {
        "fields": ["image", "name", "description", "price"],
        "add_to_cart": true,
        "quantity_selector": true
      }
    },
    "menu_item_detail_modal": {
      "customization_options": {
        "size": true,
        "toppings": true,
        "spice_level": true
      },
      "special_instructions": true,
      "quantity": true
    },
    "shopping_cart": {
      "cart_badge": true,
      "cart_sidebar_or_modal": true,
      "line_items": {
        "quantity_controls": true,
        "subtotal": true
      },
      "fees": {
        "delivery_fee": true,
        "service_fee": {
          "enabled": true,
          "percent_of_subtotal": 5
        }
      },
      "promo_codes": true,
      "checkout": true
    },
    "checkout": {
      "address_selection": true,
      "add_new_address": true,
      "delivery_time": {
        "asap": true,
        "scheduled": true
      },
      "payment_method_selection": true,
      "order_summary": true,
      "place_order": true,
      "confirmation": {
        "order_number": true
      }
    },
    "order_tracking": {
      "statuses": ["Confirmed", "Preparing", "On the way", "Delivered"],
      "eta": true,
      "driver_info": true,
      "contact_driver": true
    },
    "order_history": {
      "list": true,
      "details": true,
      "reorder": true,
      "rating": {
        "stars_1_to_5": true,
        "review_text": true
      }
    },
    "favorites": {
      "favorite_restaurants": true,
      "quick_access": "profile"
    }
  },
  "data_requirements": {
    "seed": {
      "restaurants_min": 20,
      "restaurant_categories_min": 6,
      "menu_items_min": 100,
      "promo_codes": ["SAVE10", "FIRSTORDER", "FREE5", "WELCOME15", "SHIPFREE"],
      "test_user": {
        "email": "user@foodhub.com",
        "password": "food123"
      },
      "sample_orders": {
        "include_statuses": true,
        "statuses": ["Confirmed", "Preparing", "On the way", "Delivered"]
      }
    }
  },
  "suggested_domain_model": {
    "entities": [
      "users",
      "addresses",
      "payment_methods",
      "restaurants",
      "restaurant_categories",
      "restaurant_category_map",
      "menu_categories",
      "menu_items",
      "menu_item_options",
      "cart",
      "cart_items",
      "promo_codes",
      "orders",
      "order_items",
      "drivers",
      "order_status_events",
      "favorites",
      "reviews"
    ]
  },
  "ui_pages": [
    { "route": "/", "name": "Home" },
    { "route": "/search", "name": "Search" },
    { "route": "/restaurants/:id", "name": "RestaurantDetail" },
    { "route": "/cart", "name": "Cart" },
    { "route": "/checkout", "name": "Checkout" },
    { "route": "/orders", "name": "OrderHistory" },
    { "route": "/orders/:id", "name": "OrderDetailAndTracking" },
    { "route": "/favorites", "name": "Favorites" },
    { "route": "/profile", "name": "Profile" },
    { "route": "/login", "name": "Login" },
    { "route": "/register", "name": "Register" }
  ],
  "api_expectations": {
    "base_path": "/api",
    "auth": [
      "POST /auth/register",
      "POST /auth/login",
      "GET /auth/me"
    ],
    "restaurants": [
      "GET /restaurants (filters/sort/pagination)",
      "GET /restaurants/:id",
      "GET /restaurants/:id/menu",
      "GET /categories",
      "GET /search (q, type=restaurant|item)"
    ],
    "cart": [
      "GET /cart",
      "POST /cart/items",
      "PATCH /cart/items/:id",
      "DELETE /cart/items/:id",
      "POST /cart/apply-promo",
      "DELETE /cart/promo"
    ],
    "checkout_orders": [
      "POST /orders (place order)",
      "GET /orders (user history)",
      "GET /orders/:id",
      "POST /orders/:id/reorder",
      "POST /orders/:id/rate"
    ],
    "profile": [
      "GET /addresses",
      "POST /addresses",
      "DELETE /addresses/:id",
      "GET /payment-methods",
      "POST /payment-methods",
      "DELETE /payment-methods/:id"
    ],
    "favorites": [
      "GET /favorites",
      "POST /favorites",
      "DELETE /favorites/:id"
    ]
  },
  "non_functional": {
    "responsive": true,
    "accessibility_basics": true,
    "error_states": true,
    "loading_states": true
  }
}
'})
