write_file({'path': 'design/spec.requirements.json', 'content': '{
  "project": {
    "name": "FoodHub",
    "type": "food_delivery_platform",
    "inspiration": ["DoorDash", "Uber Eats"],
    "description": "A DoorDash-style food delivery web app with restaurant browsing, menu ordering, cart/checkout, order tracking, favorites, and user profile management.",
    "testing_urls": {
      "frontend": "http://localhost:8000",
      "backend_api": "http://localhost:3000"
    }
  },
  "design": {
    "primary_color": "#FF3008",
    "style": {
      "tone": "modern, clean, high-contrast",
      "layout": "left sidebar navigation + top header + content grid",
      "component_style": "rounded cards, pill filters, gradient promo banners, image-forward store/product cards",
      "iconography": "simple line icons + emoji category pills",
      "typography": {
        "preferred": "system-ui / Inter-like",
        "hierarchy": "strong headings, compact metadata"
      }
    },
    "reference_images": [
      {
        "path": "screenshot/doordash/home.png",
        "must_match": [
          "Left sidebar nav with icons",
          "Top header with logo, search, location, delivery/pickup toggle, cart icon",
          "Category pills with emoji",
          "Promo banners (gradient cards)",
          "Restaurant cards grid with large image and metadata"
        ]
      },
      {
        "path": "screenshot/doordash/item_detail.png",
        "must_match": [
          "Store page header with cover image",
          "Circular category icons with labels",
          "Promo banner",
          "Product grid cards with + overlay"
        ]
      },
      {
        "path": "screenshot/doordash/goods.png",
        "must_match": [
          "Product detail modal",
          "Close X",
          "Large image",
          "Price with dollar sign styling",
          "Quantity +/- controls",
          "Add to cart red button",
          "Recommendations carousel",
          "Bottom sticky minimum order bar"
        ]
      }
    ],
    "ui_requirements": {
      "global": [
        "Responsive layout (desktop-first; should remain usable on tablet/mobile)",
        "Use TailwindCSS",
        "Use DoorDash red (#FF3008) for primary CTAs",
        "Cart badge count visible",
        "Loading and empty states"
      ],
      "navigation": {
        "sidebar": {
          "items": [
            "Home",
            "Grocery",
            "Retail",
            "Convenience",
            "Pickup",
            "Orders",
            "Favorites",
            "Profile"
          ]
        },
        "header": {
          "elements": [
            "Logo",
            "Search bar (restaurants + dishes)",
            "Location selector",
            "Delivery/Pickup toggle",
            "Cart icon"
          ]
        }
      },
      "homepage": {
        "sections": [
          "Category pills",
          "Promo banners",
          "Featured restaurants grid",
          "Filters + sort controls"
        ]
      },
      "restaurant_page": {
        "sections": [
          "Cover image + basic info",
          "Menu category navigation",
          "Menu items grid/list",
          "Minimum order sticky bar"
        ]
      },
      "product_modal": {
        "fields": [
          "Customization options (size, toppings, spice level)",
          "Special instructions",
          "Quantity selector",
          "Add to cart"
        ]
      },
      "cart": {
        "presentation": "sidebar/modal",
        "features": [
          "Line items with quantity +/-",
          "Subtotal",
          "Delivery fee",
          "Service fee = 5% of subtotal",
          "Promo code input",
          "Total",
          "Checkout CTA"
        ]
      }
    }
  },
  "tech_stack": {
    "frontend": {
      "framework": "React",
      "bundler": "Vite",
      "styling": "TailwindCSS",
      "state": "React Context or lightweight state management",
      "notes": ["Use fetch/axios client with JWT bearer token support"]
    },
    "backend": {
      "runtime": "Node.js",
      "framework": "Express",
      "auth": {
        "type": "JWT",
        "password_hash": "bcrypt"
      }
    },
    "database": {
      "engine": "PostgreSQL"
    }
  },
  "domain": {
    "entities": {
      "user": {
        "fields": ["id", "email", "password_hash", "full_name", "phone", "created_at"],
        "relations": ["addresses", "payment_methods", "favorites", "orders", "recent_searches"]
      },
      "address": {
        "fields": ["id", "user_id", "label", "line1", "line2", "city", "state", "postal_code", "lat", "lng", "is_default"]
      },
      "payment_method": {
        "fields": ["id", "user_id", "brand", "last4", "exp_month", "exp_year", "is_default"],
        "notes": ["No real payment processing; store tokenized-like metadata only"]
      },
      "category": {
        "fields": ["id", "name", "emoji", "slug"],
        "examples": ["Chinese", "Italian", "Fast Food", "Mexican", "Indian", "Desserts"]
      },
      "restaurant": {
        "fields": [
          "id",
          "name",
          "description",
          "category_id",
          "price_range",
          "rating",
          "review_count",
          "distance_miles",
          "delivery_time_min",
          "delivery_fee_cents",
          "min_order_cents",
          "cover_image_url",
          "card_image_url",
          "is_featured"
        ]
      },
      "menu_category": {
        "fields": ["id", "restaurant_id", "name", "sort_order", "icon_url"],
        "examples": ["Appetizers", "Main Course", "Drinks", "Desserts"]
      },
      "menu_item": {
        "fields": [
          "id",
          "restaurant_id",
          "menu_category_id",
          "name",
          "description",
          "price_cents",
          "unit_info",
          "image_url",
          "is_available",
          "stock_badge"
        ]
      },
      "menu_item_option_group": {
        "fields": ["id", "menu_item_id", "name", "selection_type", "is_required", "min_select", "max_select", "sort_order"],
        "examples": ["Size", "Toppings", "Spice level"]
      },
      "menu_item_option": {
        "fields": ["id", "option_group_id", "name", "price_delta_cents", "is_default", "sort_order"]
      },
      "cart": {
        "fields": ["id", "user_id", "restaurant_id", "created_at", "updated_at"],
        "notes": ["One active cart per user; typically tied to a single restaurant"]
      },
      "cart_item": {
        "fields": ["id", "cart_id", "menu_item_id", "quantity", "special_instructions", "selected_options_json", "unit_price_cents"]
      },
      "promo_code": {
        "fields": ["id", "code", "type", "amount_cents", "percent_off", "min_subtotal_cents", "active"],
        "examples": ["SAVE10", "FIRSTORDER", "FREE5"]
      },
      "order": {
        "fields": [
          "id",
          "order_number",
          "user_id",
          "restaurant_id",
          "address_id",
          "payment_method_id",
          "status",
          "scheduled_for",
          "subtotal_cents",
          "delivery_fee_cents",
          "service_fee_cents",
          "discount_cents",
          "total_cents",
          "placed_at"
        ],
        "status_flow": ["CONFIRMED", "PREPARING", "ON_THE_WAY", "DELIVERED"]
      },
      "order_item": {
        "fields": ["id", "order_id", "menu_item_id", "name_snapshot", "price_cents", "quantity", "selected_options_json", "special_instructions"]
      },
      "driver": {
        "fields": ["id", "name", "photo_url", "phone"],
        "notes": ["Mock driver info for tracking"]
      },
      "order_tracking": {
        "fields": ["id", "order_id", "driver_id", "eta_minutes", "last_status_at"],
        "notes": ["Can be derived from order status; stored for demo"]
      },
      "favorite_restaurant": {
        "fields": ["id", "user_id", "restaurant_id", "created_at"]
      },
      "search": {
        "recent_search": {
          "fields": ["id", "user_id", "query", "created_at"]
        },
        "suggestion": {
          "strategy": "backend returns top matches for restaurants and menu items"
        }
      },
      "review": {
        "fields": ["id", "order_id", "user_id", "restaurant_id", "rating", "text", "created_at"]
      }
    },
    "business_rules": [
      "Service fee = round(subtotal * 0.05)",
      "Cart typically restricted to one restaurant at a time; if adding from another restaurant, prompt to clear cart (MVP may auto-clear with confirmation)",
      "Promo codes apply at checkout; validate active + min subtotal",
      "Order number is human-friendly (e.g., FH-2026-000123)",
      "Favorites are per-user"
    ]
  },
  "api": {
    "base_url": "http://localhost:3000",
    "auth": {
      "scheme": "Bearer JWT",
      "endpoints_required": [
        "POST /api/auth/register",
        "POST /api/auth/login",
        "GET /api/auth/me"
      ]
    },
    "endpoints_required": {
      "restaurants": [
        "GET /api/categories",
        "GET /api/restaurants (filters: category, rating_min, max_delivery_time, price_range, distance_max; sort: rating|delivery_time|distance)",
        "GET /api/restaurants/:id",
        "GET /api/restaurants/:id/menu (includes categories and items)",
        "GET /api/search/suggestions?q= (restaurants + dishes)",
        "GET /api/search/recent (auth)",
        "POST /api/search/recent (auth)"
      ],
      "cart": [
        "GET /api/cart (auth)",
        "POST /api/cart/items (auth)",
        "PATCH /api/cart/items/:id (auth)",
        "DELETE /api/cart/items/:id (auth)",
        "POST /api/cart/apply-promo (auth)",
        "POST /api/cart/clear (auth)"
      ],
      "checkout_orders": [
        "POST /api/orders (auth)",
        "GET /api/orders (auth)",
        "GET /api/orders/:id (auth)",
        "POST /api/orders/:id/reorder (auth)",
        "POST /api/orders/:id/review (auth)"
      ],
      "tracking": [
        "GET /api/orders/:id/tracking (auth)",
        "POST /api/orders/:id/advance-status (auth; demo only)"
      ],
      "profile": [
        "GET /api/profile (auth)",
        "PUT /api/profile (auth)",
        "GET /api/addresses (auth)",
        "POST /api/addresses (auth)",
        "PATCH /api/addresses/:id (auth)",
        "DELETE /api/addresses/:id (auth)",
        "GET /api/payment-methods (auth)",
        "POST /api/payment-methods (auth)",
        "PATCH /api/payment-methods/:id (auth)",
        "DELETE /api/payment-methods/:id (auth)",
        "GET /api/favorites (auth)",
        "POST /api/favorites (auth)",
        "DELETE /api/favorites/:id (auth)"
      ]
    },
    "response_conventions": {
      "success_wrapper": {"success": true},
      "list_wrapper": {"items": [], "total": 0},
      "error_wrapper": {"success": false, "error": {"message": "string", "code": "string"}}
    }
  },
  "sample_data": {
    "restaurants_min": 20,
    "categories_min": 6,
    "menu_items_min": 100,
    "promo_codes": [
      {"code": "SAVE10", "type": "percent", "percent_off": 10, "min_subtotal_cents": 2000},
      {"code": "FIRSTORDER", "type": "percent", "percent_off": 15, "min_subtotal_cents": 2500},
      {"code": "FREE5", "type": "amount", "amount_cents": 500, "min_subtotal_cents": 1500},
      {"code": "WELCOME7", "type": "amount", "amount_cents": 700, "min_subtotal_cents": 3000},
      {"code": "DINNER20", "type": "percent", "percent_off": 20, "min_subtotal_cents": 5000}
    ],
    "test_user": {
      "email": "user@foodhub.com",
      "password": "food123",
      "full_name": "FoodHub User"
    },
    "sample_orders": {
      "include_statuses": ["CONFIRMED", "PREPARING", "ON_THE_WAY", "DELIVERED"],
      "count_min": 6
    }
  },
  "non_functional": {
    "security": [
      "Passwords stored as bcrypt hashes",
      "JWT expiration (e.g., 24h)",
      "Auth required for cart/checkout/orders/profile/favorites"
    ],
    "performance": ["Paginate restaurant lists when needed"],
    "usability": [
      "Clear empty states",
      "Form validation and readable error messages",
      "Optimistic UI allowed for cart quantity updates"
    ]
  }
}
'})

